import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Contact, contacts } from '../contacts';

@Component({
  selector: 'app-contact-info',
  templateUrl: './contact-info.component.html',
  styleUrls: ['./contact-info.component.css'],
})
export class ContactInfoComponent implements OnInit {
  contact: Contact | undefined;
  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    const routeParams = this.route.snapshot.paramMap;
    const contactIDFromRoute = routeParams.get('contactId');

    this.contact = contacts.find(
      (contact) => contact._id === contactIDFromRoute
    );
  }
}
<h2>Contact Information</h2>

<div *ngIf="contact">
  <h3>Name: {{ contact.FirstName }} {{ contact.LastName }}</h3>
  <h4>Number: {{ contact.PhoneNo }}</h4>
  <h4>email: {{ contact.emailID }}</h4>
  <h4>City: {{ contact.CityName }}</h4>
  <button [routerLink]="['/update',contact._id]" type="button">Update</button>
  <h1></h1>
  <button type="button">Delete</button>
</div>
