import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.css'],
})
export class UpdateComponent {
  contactForm = this.formBuilder.group({
    FirstName: '',
    LastName: '',
    PhoneNo: '',
    emailID: '',
    CityName: '',
  });
  constructor(private formBuilder: FormBuilder, private route: ActivatedRoute) {}

  onSubmit(): void {}

  update() {
    window.alert('Contact has been updated!');
  }
}
<h2>Enter Updated Details:</h2>

<form [formGroup]="contactForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="FirstName"> First Name </label>
    <input id="FirstName" type="text" formControlName="FirstName" />
  </div>

  <div>
    <label for="LastName"> Last Name </label>
    <input id="LastName" type="text" formControlName="LastName" />
  </div>

  <div>
    <label for="PhoneNo"> Phone No </label>
    <input id="PhoneNo" type="text" formControlName="PhoneNo" />
  </div>

  <div>
    <label for="emailID"> email id</label>
    <input id="emailID" type="text" formControlName="emailID" />
  </div>

  <div>
    <label for="CityName"> City Name</label>
    <input id="CityName" type="text" formControlName="CityName" />
  </div>
  <button type="button" (click)="update()">Update</button>
</form>
